import React, { Component } from 'react'; 
import { withRouter } from 'react-router-dom';
import { Axios_Instance} from '../utils/axiosInterceptor';
import { BASEURL, ROUTES} from '../config/routes';
import { checkAuthorization, getUserDetails } from '../utils';

class Dashboard extends Component {
    constructor(){
        super(); 
        this.state = {
            user:{},
            wishTitle : 'Good Morning',
            feeds:[]
        }
    }

    componentDidMount(){
        checkAuthorization(this.props);
        this.setWishTitle();
        let details = getUserDetails();
        this.setState({user:details});
        this.fetchFeeds();

    };

    setWishTitle(){
        let {wishTitle} = this.state;
        let currentDate = new Date();
        let currentTime = currentDate.toString().split(' ')[4]; 
        let timeSegments = currentTime.split(':');
        if(timeSegments >= '12'){
            wishTitle = 'Good After Noon';
        }
        if(timeSegments >= '17'){
            wishTitle = 'Good Evening';
        }
        this.setState({wishTitle});
    }; 

    async fetchFeeds(){
        try{
            let _resp = await Axios_Instance.get(`${ROUTES.event_feeds}`);
            if(!_resp){
                _resp = {data:{success:false, msg:'Unexpected error occured'} };
                return;
            }
            if(_resp.data && _resp.data.success){
                let { feeds } = this.state;
                feeds = _resp.data.data.events;
                this.setState({ feeds });
            }
        }catch(ex){
            Notification({
                show:true,
                data:ex.response ?ex.response.data : ex});
        } 
    }

  render() {
            return (
                <div className="main-container">
                    <div className="container">
                        <h4 className="page-title">
                            <p style={{'textTransform':'capitalize'}}>{this.state.wishTitle}! {this.state.user.first_name || 'Guest'}</p>
                        </h4> 
                        <div className="row">
                            <div className="col-md-6 col-sm-6">
                                <p><b>Feed</b></p><hr />
                            </div>
                            <div className="col-md-6 col-sm-6 borderLeft" >
                                <p><b>Live</b></p><hr />
                                <div>
                                    <div className="">
                                        <p><b>Today</b></p>
                                        <div>
                                            <div className="row">
                                                {this.state.feeds.map((obj, k)=>
                                                    <div className="col-sm-6 col-md-6">
                                                        <div className="card">
                                                            <div className="LiveCard">
                                                                <img src={`${BASEURL}${obj.thumbnail}`} className="live_img" alt="test"/>
                                                                <p className="live_title">{obj.title || '--'}</p>
                                                                <p style={{"fontSize":"11px"}}>{obj.channel_name || '--'}</p>
                                                                <p style={{"fontSize":"10px"}}><span>{obj.start_time || '--'}</span> - <span>{obj.expected_end_time || '--'}</span> </p>
                                                                <div>
                                                                    <button className="btn" style={{"fontSize":"10px"}}>Join Live</button>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                )}

                                            </div>
                                        </div>
                                        <p><b>Tomorrow</b></p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>
            );
    }

}

export default withRouter(Dashboard);
