import React, { Component } from 'react'; 
import { withRouter,Link } from 'react-router-dom';
import { Axios } from '../utils/axiosInterceptor';
import { BASEURL, ROUTES} from '../config/routes';
import {  CONFIGS} from '../config';
import {Notification} from '../utils';

class Signup extends Component {
    constructor(){
        super(); 
        this.state = {
            form:{email:'',password:''}
        }
    }

    _handleFormChange(e){
        let {form} = this.state;
        form[e.target.name] = e.target.value;
        this.setState({form});
      };

      async _onFormSubmit(e){
        e.preventDefault();
        let {form} = this.state;
        if(!form.email || !form.password){
            alert('Please enter email and password');
            return;
        }
        try{
          let _resp = await Axios.post(`${BASEURL}${ROUTES.login}`,form);
          if(!_resp && !_resp.data){_resp = {data:{success:false, msg:'Unexpected erro occured'} };}
          console.log({success:_resp.data.success, msg:_resp.data.error || _resp.data.message})
          if(_resp.data.success){
              localStorage.setItem(CONFIGS.uLocal, _resp.data.data.token);
              this.props.history.push('/dashboard');
          }else{
            Notification({
                show: true,
                data:{success:_resp.data.success, msg:_resp.data.error || _resp.data.message}
              });
          }
        }catch(ex){
            console.log(ex.response)
          Notification({
            show:true,
            data:{success:false, msg:ex.response ?ex.response.data.error : ex}
        });}

      };

  render() {
            return (
                <div className="inner-container">
                    <div className="container">
                        <div className="row">
                        <div className="col-md-4 col-sm-4"></div>
                        <div className="col-md-4 col-sm-4">
                            <div className="logo_plain text-center"><b>BOCADEE</b></div>
                            <div className="card">

                            <div className="contctxt">User Login</div><br /><br />
                            <div className="formint conForm">
                                <form>
                                <div className="input-wrap has-float-label">
                                    <input type="text" name="email" className="form-control" onChange={this._handleFormChange.bind(this)} />
                                    <span>Email</span>
                                </div>
                                <div className="input-wrap has-float-label">
                                    <input type="password" name="password" className="form-control" onChange={this._handleFormChange.bind(this)}/>
                                    <span>Password</span>
                                </div>
                                <div className="sub-btn text-center">
                                    <button className="btn" onClick={this._onFormSubmit.bind(this)}>LOGIN</button>
                                </div>
                                <div className="newuser"><i className="fa fa-user" aria-hidden="true"></i> New User? <Link to='/signup'>Signup</Link></div>
                                </form>
                            </div>
                            </div>
                        </div>
                        <div className="col-md-4 col-sm-4"></div>
                        </div>
                    </div>
                    </div>
            );
    }

}

export default withRouter(Signup);
