import React, { Component } from 'react'; 
import { withRouter, Link } from 'react-router-dom';

class ApartmentDetails extends Component {
    constructor(){
        super(); 
        this.state = {
            details:[],
            selectedApartmentId:'',
            selectedApartment:{}
        }
    }

    componentDidMount(){
        let { selectedFloor } = this.props;
        let { details } = this.state;
        if(selectedFloor){
            details = selectedFloor.details || [];
            this.setState({details});
        }
    }; 
    _selectApartment(apartment){
        let { selectedApartment, selectedApartmentId} = this.state;
        selectedApartment = apartment;
        selectedApartmentId = apartment._id;
        this.setState({selectedApartment, selectedApartmentId});
    }

    _apartmentDetailsSection(){
        return(
            <div className="aprtmnt_section">Details</div>
        )
    }

  render() {
            if(!this.state.details.length){return('Loading...')}
            console.log(this.state.details)
            return (
                <div className="popup-overlay">
                    <div className="container">
                        <div className="row">
                            <div className="col-md-2 col-sm-2 col-xs-12"></div>
                            <div className="col-md-8 col-sm-8 col-xs-12">
                                <h5>Apartment Details</h5>
                                    <div class="panel-group" id="accordion">
                                        {this.state.details.map((apartment,k)=>
                                            <>
                                            <div class="panel panel-default">
                                                <div class="panel-heading">
                                                    <h4 class="panel-title">
                                                        <a data-toggle="collapse" data-parent="#accordion" href="#collapse1">
                                                            Apartment No. {apartment.apartment_no}</a>
                                                    </h4>
                                                </div>
                                                <div id="collapse1" class="panel-collapse collapse in">
                                                    <div class="panel-body">Lorem ipsum dolor sit amet, consectetur adipisicing elit,
                                                    sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad
                                                    minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea
                                                    commodo consequat.</div>
                                                </div>
                                            </div>  

                                            </>
                                            )}
                                    </div>
                            </div>
                            <div className="col-md-2 col-sm-2 col-xs-12">
                                <span> <i className="icon-close icons theme-color cursor"></i></span>
                            </div>
                        </div>

                    </div>
                </div>
            );
    }
}

export default withRouter(ApartmentDetails);
