import React, { Component } from 'react'; 
import { withRouter,Link } from 'react-router-dom';
import {CONFIGS} from '../../config';
import { Axios_Instance, Ax } from '../../utils/axiosInterceptor';
import { BASEURL, ROUTES} from '../../config/routes';

class Header extends Component {
    constructor(){
        super(); 
        this.state = {
        }
    }

    componentDidMount(){
        this.fetchChannels();
    }

    _logout(){
        localStorage.removeItem(CONFIGS.uLocal);
        this.props.history.push('/');
    }

    async fetchChannels(){
        try{
            let _resp = await Axios_Instance.get(`${ROUTES.fetch_channels}`);
            if(!_resp && !_resp.data){
                _resp = {data:{success:false, msg:'Unexpected error occured'} };
                return;
            }
            if(_resp.data && _resp.data.success){
                let { form } = this.state;
                form.response = _resp.data.data.channels;
                this.setState({ form });
                console.log(this.state.form)
            }
        }catch(ex){
            Notification({
                show:true,
                data:ex.response ?ex.response.data : ex});
        } 
    }

  render() {
    let urlName = window.location.pathname;
    if(urlName === '/' || urlName === '/signin' || urlName === '/signup') return null;
    return (
        <div className="header-wrap">
        <div className="container"> 
            <div className="row"> 
            <div className="col-md-3 col-sm-3 col-xs-6">
               <b>CRUX</b>
            </div>
            <div className="col-md-7 col-sm-6 hidden-xs"> 
            </div>
            <div className="col-md-2 col-sm-3 col-xs-6">
                <div className="pull-right">
                    <select>
                        <option value="eds">sdwsadsd</option>
                        <option value="eds">sdwsadsd</option>
                    </select>&nbsp;&nbsp;&nbsp;&nbsp;
                    {/* <i className="icon-bell icons"></i>&nbsp;&nbsp; */}

                    <div className="dropdown">
                        <span className="dropbtn"> <i className="icon-user icons"></i></span>
                        <div className="dropdown-content">
                            <a href="/channels" ><i className="icon-logout icons"></i>&nbsp;Your Channels/Pages</a>
                            <a href="javascript:void" onClick={this._logout.bind(this)}><i className="icon-logout icons"></i>&nbsp;Logout</a>
                        </div>
                        </div>
                </div>
            </div>
            </div>
        </div>
        </div>
    );
    }

}

export default withRouter(Header);
