                        </div>
                    </div>
                </>
            );
    }

}
const mapDispatchToProps = (dispatch)=>{
    return{
      'selectFloor':(data)=>dispatch({'type':ACTION_TYPES.SELECTED_FLOORS,data})
    }
  }

export default connect(null,mapDispatchToProps)(withRouter(ManageTenants));
