import React, { Component } from 'react'; 
import { withRouter } from 'react-router-dom';
import { checkAuthorization, getUserDetails } from '../utils';
import OwnerDashboard from './subComponents/owner-dashboard';
import TenantDashboard from './subComponents/tenant-dashboard';

class Dashboard extends Component {
    constructor(){
        super(); 
        this.state = {
            user:{},
            wishTitle : 'Good Morning'
        }
    }

    componentDidMount(){
        checkAuthorization(this.props);
        this.setWishTitle();
        let details = getUserDetails();
        this.setState({user:details});

    };

    setWishTitle(){
        let {wishTitle} = this.state;
        let currentDate = new Date();
        let currentTime = currentDate.toString().split(' ')[4]; 
        let timeSegments = currentTime.split(':');
        if(timeSegments >= '12'){
            wishTitle = 'Good After Noon';
        }
        if(timeSegments >= '17'){
            wishTitle = 'Good Evening';
        }
        this.setState({wishTitle});
    }; 

  render() {
            return (
                <div className="main-container">
                    <div className="container">
                        <h4 className="page-title">
                            <p style={{'textTransform':'capitalize'}}>{this.state.wishTitle}! {this.state.user.first_name || 'Guest'}</p>
                        </h4> 
                        <div className="row">
                            <div className="col-md-6 col-sm-6">
                                <p><b>Feed</b></p><hr />
                            </div>
                            <div className="col-md-6 col-sm-6 borderLeft" >
                                <p><b>Live</b></p><hr />
                                <div>
                                    <div className="">
                                        <p><b>Today</b></p>
                                        <div>
                                            <div className="row">
                                                <div className="col-sm-6 col-md-6">
                                                    <div className="card">
                                                        <div className="LiveCard">
                                                            <img src="https://miro.medium.com/max/1400/1*1dzH5UxzoQS7LDpMUOOm6A.webp" className="live_img" alt="test"/>
                                                            <p className="live_title">Saurabh sharma is going</p>
                                                            <p>Sanatani Talks</p>
                                                            <p style={{"fontSize":"10px"}}><span>15-03-2022 10:00PM</span> - <span>15-03-2022 10:00PM</span> </p>
                                                            <div>
                                                                <button className="btn" style={{"fontSize":"10px"}}>Join Live</button>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-6">
                                                    <div className="card">
                                                        <div className="LiveCard">
                                                                <img src="https://miro.medium.com/max/1400/1*1dzH5UxzoQS7LDpMUOOm6A.webp" className="live_img" alt="test"/>
                                                                <p className="live_title">Saurabh sharma is going</p>
                                                                <p>Sanatani Talks</p>
                                                                <p style={{"fontSize":"10px"}}><span>15-03-2022 10:00PM</span> - <span>15-03-2022 10:00PM</span> </p>
                                                                <div>
                                                                    <button className="btn" style={{"fontSize":"10px"}}>Join Live</button>
                                                                </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                        <p><b>Tomorrow</b></p>
                                        <div>
                                            <div className="row">
                                                    <div className="col-sm-6 col-md-6">
                                                        <div className="card">
                                                            <div className="LiveCard">
                                                                <img src="https://miro.medium.com/max/1400/1*1dzH5UxzoQS7LDpMUOOm6A.webp" className="live_img" alt="test"/>
                                                                <p className="live_title">Saurabh sharma is going</p>
                                                                <p>Sanatani Talks</p>
                                                                <p style={{"fontSize":"10px"}}><span>15-03-2022 10:00PM</span> - <span>15-03-2022 10:00PM</span> </p>

                                                            </div>
                                                        </div>
                                                    </div>
                                                    <div className="col-sm-6 col-md-6">
                                                        <div className="card">
                                                            <div className="LiveCard">
                                                                    <img src="https://miro.medium.com/max/1400/1*1dzH5UxzoQS7LDpMUOOm6A.webp" className="live_img" alt="test"/>
                                                                    <p className="live_title">Saurabh sharma is going</p>
                                                                    <p>Sanatani Talks</p>
                                                                    <p style={{"fontSize":"10px"}}><span>15-03-2022 10:00PM</span> - <span>15-03-2022 10:00PM</span> </p>

                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>
            );
    }

}

export default withRouter(Dashboard);
