import React, { Component } from 'react'; 
import { withRouter,Link } from 'react-router-dom';
import { Axios } from '../utils/axiosInterceptor';
import { BASEURL, ROUTES} from '../config/routes';
import { configs} from '../config';
import { SIGNUP_REQ_VALIDATOR} from '../utils/validators';
import {Notification} from '../utils';
console.log(configs)
class Signup extends Component {
    constructor(){
        super(); 
        this.state = {
            form:{mobile:'',password:''}
        }
    }

    _handleFormChange(e){
        let {form} = this.state;
        form[e.target.name] = e.target.value;
        this.setState({form});
        console.log(this.state.form)
      };

      async _onFormSubmit(e){
        e.preventDefault();
        let {form} = this.state;
        // let _error = SIGNUP_REQ_VALIDATOR(form);
        // console.log(_error);
        // if(_error.is_error) return;
        try{
          let _resp = await Axios.post(`${BASEURL}${ROUTES.signin}`,form);
          if(!_resp && !_resp.data){_resp = {data:{success:false, msg:'Unexpected erro occured'} };}
          Notification({
            show: true,
            data:{success:_resp.data.success, msg:_resp.data.message || _resp.data.msg}
          });

        }catch(ex){
          Notification({
            show:true,
            data:ex.response ?ex.response.data : ex});
        }

      };

  render() {
            return (
                <div className="inner-container">
                    <div className="container">
                        <div className="row">
                        <div className="col-md-4 col-sm-4"></div>
                        <div className="col-md-4 col-sm-4">
                            <div className="logo_plain text-center"><b>RENT HAWK</b></div>
                            <div className="card">

                            <div className="contctxt">Owner Login</div>
                            <div className="formint conForm">
                                <form>
                                <div className="input-wrap has-float-label">
                                    <input type="text" name="mobile" className="form-control" onChange={this._handleFormChange.bind(this)} />
                                    <span>Mobile No.</span>
                                </div>
                                <div className="input-wrap has-float-label">
                                    <input type="password" name="password" className="form-control" onChange={this._handleFormChange.bind(this)}/>
                                    <span>Password</span>
                                </div>
                                <div className="sub-btn text-center">
                                    <button className="btn" onClick={this._onFormSubmit.bind(this)}>LOGIN</button>
                                </div>
                                <div className="newuser"><i className="fa fa-user" aria-hidden="true"></i> New User? <Link to='/singup'>Signup</Link></div>
                                </form>
                            </div>
                            </div>
                        </div>
                        <div className="col-md-4 col-sm-4"></div>
                        </div>
                    </div>
                    </div>
            );
    }

}

export default withRouter(Signup);
