import { ACTION_TYPES} from '../utils/actions';

const isState={
    notification:{'show':false,'msg':''},
};

const reducer = (state=isState,action)=>{
    switch(action.type){
        case ACTION_TYPES.TOGGLE_NOTIFICATION:
            return{
                ...state,
                notification : action.data
            }
        default:
            return state;
    }
};

export default reducer;
