import React, { Component } from 'react'; 
import { withRouter } from 'react-router-dom';
import { checkAuthorization, getUserDetails } from '../utils'

class Dashboard extends Component {
    constructor(){
        super(); 
        this.state = {
            user:{},
            wishTitle : 'Good Morning'
        }
    }

    componentDidMount(){
        checkAuthorization(this.props);
        this.setWishTitle();
        let details = getUserDetails();
        this.setState({user:details});

    };

    setWishTitle(){
        let {wishTitle} = this.state;
        let currentDate = new Date();
        let currentTime = currentDate.toString().split(' ')[4]; 
        let timeSegments = currentTime.split(':');
        if(timeSegments >= '12'){
            wishTitle = 'Good After Noon';
        }
        if(timeSegments >= '17'){
            wishTitle = 'Good Evening';
        }
        this.setState({wishTitle});
    };

    _redirectTo(){
        this.props.history.push('/add-building');
    }

  render() {
            return (
                <div className="main-container">
                <div className="container">
                    <h4 className="page-title">
                        <p style={{'textTransform':'capitalize'}}>{this.state.wishTitle}! {this.state.user.first_name || 'Guest'}</p>
                    </h4>

                    <div className="row">
                        <div className="col-md-3 col-sm-4 col-xs-6">
                            <div className="card">
                                <div className="card-body">
                                    <i className="icon-home icons card_icon"></i>
                                     <h5>Sundar Farm House</h5>
                                     <div className="row">
                                        <div className="col-md-6 col-sm-6">
                                            <p>Floors: <span>5</span></p>
                                        </div>
                                        <div className="col-md-6 col-sm-6">
                                            <p>Apartments: <span>5</span></p>
                                        </div>
                                     </div>
                                </div>
                            </div>
                        </div>
                        <div className="col-md-3 col-sm-4 col-xs-6">
                            <div className="card">
                                <div className="card-body">
                                    sdfsdf
                                </div>
                            </div>
                        </div>
                        <div className="col-md-3 col-sm-4 col-xs-6">
                            <div className="card">
                                <div className="card-body">
                                    sdfsdf
                                </div>
                            </div>
                        </div>
                        <div className="col-md-3 col-sm-4 col-xs-6">
                            <div className="card">
                                <div className="card-body">
                                    sdfsdf
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="add_btn cursor" title="Add building" onClick={this._redirectTo.bind(this)}><span className="add_btn_inner">+</span></div>
                </div>
            );
    }

}

export default withRouter(Dashboard);
