import React, { Component } from 'react'; 
import { withRouter } from 'react-router-dom';
import { checkAuthorization, getUserDetails } from '../utils'

class Dashboard extends Component {
    constructor(){
        super(); 
        this.state = {
            user:{}
        }
    }

    componentDidMount(){
        checkAuthorization(this.props);
        let details = getUserDetails();
        this.setState({user:details});

    }

  render() {
            return (
                <div className="main-container">
                <div className="container">
                    <p className="page-title"><b style={{'textTransform':'capitalize'}}>Good Morning! {this.state.user.first_name || 'Guest'}</b></p>

                    <div className="row">
                        <div className="col-md-3 col-sm-4 col-xs-6">
                            <div className="card">
                                <div className="card-body">
                                {/* <img src="./assets/images/building.jpg" alt="building" /> */}
                                <i className="icon-power icons"></i>
                                sdfsdf
                                </div>
                            </div>
                        </div>
                        <div className="col-md-3 col-sm-4 col-xs-6">
                            <div className="card">
                                <div className="card-body">
                                    sdfsdf
                                </div>
                            </div>
                        </div>
                        <div className="col-md-3 col-sm-4 col-xs-6">
                            <div className="card">
                                <div className="card-body">
                                    sdfsdf
                                </div>
                            </div>
                        </div>
                        <div className="col-md-3 col-sm-4 col-xs-6">
                            <div className="card">
                                <div className="card-body">
                                    sdfsdf
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                </div>
            );
    }

}

export default withRouter(Dashboard);
