import React, { Component } from 'react'; 
import { withRouter,Link } from 'react-router-dom';
import { Axios_Instance } from '../utils/axiosInterceptor';
import { BASEURL, ROUTES} from '../config/routes';
import { checkAuthorization, getUserDetails } from '../utils';
import OwnerDashboard from './subComponents/owner-dashboard';
import TenantDashboard from './subComponents/tenant-dashboard';
import { BUILDING_REQ_VALIDATOR } from '../utils/validators';
import {Notification} from '../utils';
import Axios from 'axios';

class Dashboard extends Component {
    constructor(){
        super(); 
        this.state = {
            user:{},
            wishTitle : 'Good Morning',
            buildings:[]
        }
    }

    componentDidMount(){
        checkAuthorization(this.props);
        this.setWishTitle();
        let details = getUserDetails();
        this.setState({user:details});
        this.fetchBuildings();

    };

    setWishTitle(){
        let {wishTitle} = this.state;
        let currentDate = new Date();
        let currentTime = currentDate.toString().split(' ')[4]; 
        let timeSegments = currentTime.split(':');
        if(timeSegments >= '12'){
            wishTitle = 'Good After Noon';
        }
        if(timeSegments >= '17'){
            wishTitle = 'Good Evening';
        }
        this.setState({wishTitle});
    };

    _redirectTo(){
        this.props.history.push('/add-building');
    };

    async fetchBuildings(){
        try{
            let { buildings} = this.state;
            let _resp = await Axios_Instance.get(`${ROUTES.add_building}`);
            if(!_resp && !_resp.data){_resp = {data:{success:false, msg:'Unexpected erro occured'} };}
            buildings = _resp.data.data;
            this.setState({buildings});
        }catch(ex){
            Notification({
                show:true,
                data:ex.response ?ex.response.data : ex});
            }

    }

  render() {
            return (
                <div className="main-container">
                <div className="container">
                    <h4 className="page-title">
                        <p style={{'textTransform':'capitalize'}}>{this.state.wishTitle}! {this.state.user.first_name || 'Guest'}</p>
                    </h4>
                    <OwnerDashboard /> 
                </div>

                </div>
            );
    }

}

export default withRouter(Dashboard);
