export const SIGNUP_REQ_VALIDATOR = (req)=>{
    let _error = {is_error:false, msg:''};
    if(!req) _error = {is_error:true, msg:'Please fill required details'}
    if(!req.hasOwnProperty('first_name') || !req['first_name']) _error = {is_error:true, msg:'First name is required'};
    if(!req.hasOwnProperty('email') || !req['email']) _error = {is_error:true, msg:'Email is required'};
    if(!req.hasOwnProperty('password') || !req['password']) _error = {is_error:true, msg:'Password is required'};
    if(!req.hasOwnProperty('cnfpassword') || !req['cnfpassword']) _error = {is_error:true, msg:'Repeat Password is required'};
    if(req['password'] != req['cnfpassword']) _error = {is_error:true, msg:'Password & Repeat password not matched'};
    if(!req.hasOwnProperty('user_type') || !req['user_type']) _error = {is_error:true, msg:'User Type is required'};

    return _error;
};

export const BUILDING_REQ_VALIDATOR = (req)=>{
    let _error = {is_error:false, msg:''};
    if(!req) _error = {is_error:true, msg:'Please fill required details'}
    if(!req.hasOwnProperty('registered_owner_name') || !req['registered_owner_name']) _error = {is_error:true, msg:'Registered owner name is required'};
    if(!req.hasOwnProperty('total_apartments') || !req['total_apartments']) _error = {is_error:true, msg:'Total apartments is required'};
    if(!req.hasOwnProperty('total_floors') || !req['total_floors']) _error = {is_error:true, msg:'Total Floors is required'};
    if(!req.hasOwnProperty('address') || !req['address']) _error = {is_error:true, msg:'Address is required'};
    if(!req.address.hasOwnProperty('address1') || !req.address['address1']) _error = {is_error:true, msg:'Address1 is required'};
    if(!req.address.hasOwnProperty('pincode') || !req.address['pincode']) _error = {is_error:true, msg:'Pincode is required'};
    return _error;
}
