import Store from '../store';
import { ACTION_TYPES } from './actions';
import { CONFIGS } from "../config";
import decode from 'jwt-decode';

export const Notification = (data)=>{
    Store.dispatch({ type:ACTION_TYPES.TOGGLE_NOTIFICATION, ...data});
};

export const checkAuthorization = (props)=>{
    let isAuth = false;
    let token = localStorage.getItem(CONFIGS.uLocal);
    if(token && token.length > 100) isAuth = true;
    if(!isAuth) props.history.push('/');
};

export const getUserDetails = ()=>{
    let token = localStorage.getItem(CONFIGS.uLocal);
    let userDetails = {};
    if(token && token.length > 100){
        userDetails = decode(token);
    };
    return userDetails;
}

export const FormatDate = (date)=>{
    if(!date) return date;
    let d = new Date(date);
    let year = d.getUTCFullYear();
    let month = (d.getUTCMonth()+1) < 10 ? '0'+(d.getUTCMonth()+1) : d.getUTCMonth()+1;
    let _date = d.getUTCDate() < 10 ? '0'+d.getUTCDate() : d.getUTCDate();
    let hour = d.getUTCHours() < 10 ? '0'+d.getUTCHours() :d.getUTCHours();
    let minute = d.getUTCMinutes() < 10 ? '0'+d.getUTCMinutes() :d.getUTCMinutes();

    return `${year}/${month}/${_date}, ${hour}:${minute}`;
}

export const GetStatusByDateTime = (startTime, endTime) => {
    if(!startTime) return startTime;
    if(!endTime) return endTime;
    if(new Date(startTime).setHours(0,0,0,0) <= new Date().setHours(0,0,0,0)){
        if(new Date(endTime).setHours(0,0,0,0) >= new Date().setHours(0,0,0,0)){
            if(new Date(startTime) <= new Date() ){
                if(new Date(endTime) >= new Date()){
                    return "Running";
                }else{
                    return "End";
                }
            }else{
                return "Upcoming";
            }
        }else{
            return "End";
        }
    }else{
        return "Upcoming";
    }


}
