import { ACTION_TYPES} from '../utils/actions';

const isState={
    toggleNotification:false,
};

const reducer = (state=isState,action)=>{
    switch(action.type){
        case ACTION_TYPES.TOGGLE_NOTIFICATION:
            return{
                ...state,
                toggleNotification : !state.toggleNotification
            }
        default:
            return state;
    }
};

export default reducer;
