import React, { Component } from 'react'; 
import { withRouter } from 'react-router-dom';
import Notification from './subComponents/notification';
import { connect } from 'react-redux';
import { ACTION_TYPES } from '../utils/actions';


class AppContainer extends Component {

  constructor(props){
    super(props);
    this.state = {
    }

  }
  componentDidMount() { 
  } 

  render() {
    if(this.props.notification.show){
      setTimeout(()=>{
        this.props.notification = {show:false,data:{}};
      },5000);
    }
    return (
      <div>
          {this.props.children}
          {this.props.notification.show && <Notification {...this.props.notification} />}
      </div>
    );
  }
}

const mapStateToProps = (state)=>{
    return{
      'notification':state.notification
    }
  };

export default connect(mapStateToProps)(withRouter(AppContainer));
