                                    <div className="">
                                        <p><b>Today</b></p>
                                        <div>
                                            <div className="row">
                                                <div className="col-sm-6 col-md-6">
                                                    <div className="card">
                                                        <div className="LiveCard">
                                                            <img src="" width="100px" height="200px" alt="test"/>
