import React, { Component } from 'react'; 
import { withRouter } from 'react-router-dom';
import { Axios } from '../utils/axiosInterceptor';
import { BASEURL, ROUTES} from '../config/routes';
import { connect } from 'react-redux';

import {Notification} from '../utils';


class Signup extends Component {
    constructor(){
        super(); 
        this.state = {
          form:{
            first_name:'',
            last_name:'',
            email:'',
            mobile:'',
            password:'',
            cnfpassword:'',
            user_type:''
          }
        }
    }

  _handleFormChange(e){
    let {form} = this.state;
    form[e.target.name] = e.target.value;
    this.setState({form});
    console.log(this.state.form)
  };

  async _onFormSubmit(e){
    e.preventDefault();
    let {form} = this.state;
    try{
      let _resp = await Axios.post(`${BASEURL}${ROUTES.signup}`,form);
      if(!_resp && !_resp.data){_resp = {data:{success:false, msg:'Unexpected erro occured'} };}
      Notification({
        show: true,
        data:{success:_resp.data.success, msg:_resp.data.message || _resp.data.msg}
      });

    }catch(ex){
      Notification({
        show:true,
        data:ex.response ?ex.response.data : ex});
    }

  };

  render() {
            return (
                <div className="inner-container">
                <div className="container">
                  <div className="row">
                    <div className="col-md-4 col-sm-4"></div>
                    <div className="col-md-4 col-sm-4">
                      <div className="logo_plain text-center"><b>RENT HAWK</b></div>
                      <div className="card">

                        <div className="contctxt">Owner Register</div>
                        <div className="formint conForm">
                          <form>
                            <div className="input-wrap has-float-label">
                              <input type="text" name="first_name" onChange={this._handleFormChange.bind(this)} className="form-control" />
                              <span>First Name</span>
                            </div>
                            <div className="input-wrap has-float-label">
                              <input type="text" name="last_name" onChange={this._handleFormChange.bind(this)}  className="form-control" />
                              <span>Last Name</span>
                            </div>
                            <div className="input-wrap has-float-label">
                              <input type="text" name="email" onChange={this._handleFormChange.bind(this)}  className="form-control" />
                              <span>Email</span>
                            </div>
                            <div className="input-wrap has-float-label">
                              <input type="text" name="mobile" onChange={this._handleFormChange.bind(this)}  className="form-control" />
                              <span>Mobile</span>
                            </div>
                            <div className="input-wrap has-float-label">
                              <input type="password" name="password" onChange={this._handleFormChange.bind(this)}  className="form-control" />
                              <span>Passowrd</span>
                            </div>
                            <div className="input-wrap has-float-label">
                              <input type="password" name="cnfpassword" onChange={this._handleFormChange.bind(this)}  className="form-control" />
                              <span>Repeat Passowrd</span>
                            </div>
                            <div className="input-wrap has-float-label">
                              <select name="user_type" onChange={this._handleFormChange.bind(this)}  className="form-control">
                                <option>--</option>
                                <option value="owner">Owner </option>
                                <option value="tenant">Tenant</option>
                              </select>
                              <span>User Type</span>
                            </div>


                            <div className="sub-btn text-center">
                                <button className="btn" onClick={this._onFormSubmit.bind(this)}>REGISTER</button>
                            </div>
                            <div className="newuser"><i className="fa fa-user" aria-hidden="true"></i> Already have an account? <a href="#.">Login Here</a></div>
                          </form>
                        </div>
                      </div>
                    </div>
                    <div className="col-md-4 col-sm-4"></div>
                  </div>
                </div>
              </div>
            );
    }

}

// const mapDispatchToProps = (dispatch)=>{
//   return{
//     'notification':(data)=>dispatch({'type':ACTION_TYPES.TOGGLE_NOTIFICATION, ...data})
//   }
// }

export default withRouter(Signup);
