import React, { Component } from 'react'; 
import { withRouter } from 'react-router-dom';
import { Axios_Instance } from '../utils/axiosInterceptor';
import { BASEURL, ROUTES} from '../config/routes';
import { checkAuthorization, getUserDetails } from '../utils';
import { BUILDING_REQ_VALIDATOR } from '../utils/validators';
import {Notification} from '../utils';

class AddBuilding extends Component {
    constructor(){
        super(); 
        this.state = {
            user:{},
            form:{
                'name':'',
                'total_floors':'',
                'total_apartments':'',
                'address':{
                    'address1':'',
                    'address2':'',
                    'pincode':''
                },
                'registered_owner_name':'',
                'floors':[]

            }
        }
    }

    componentDidMount(){
        checkAuthorization(this.props);
        let details = getUserDetails();
        this.setState({user:details});
    };

    _onFormFieldChange(section, f_indx,a_indx, e){
        let { form } =  this.state;
        let emdObj = form;
        if(section === 'address'){
            emdObj = form.address;
        };
        if(section === 'floor' && f_indx > -1){
            emdObj = form.floors[f_indx];
        };
        if(section === 'aptmnt' && f_indx > -1 && a_indx > -1){
            emdObj = form.floors[f_indx].details[a_indx];
        }
        emdObj[e.target.name] = e.target.value;
        this.setState({form});
    };

    _appendFloorDetails(){
        let { form } = this.state;
        let total_f_no = form.floors.length;
            form.floors.push({
                'floor_no':total_f_no+1,
                'apartments':'',
                'details':[{
                    'apartment_no':'',
                    'is_vacant':true,
                    'type':'',
                    'rent_amount':0,
                    'security_amount':0,
                    'maintenance_charges':0,
                    'electricity_charges':0,
                    'water_charges':0,
                    'gas_charges':0,
                    'amenities':{},
                    "tenants_allowed":0,
                    "tenants":[{
                        "tenant_id":"",
                        "start_date":"",
                        "end_date":""
                    }]
                }]
            });
        this.setState({form});
    };

    _addApartment(floor_no){
        if(!isNaN(floor_no)){
            let { form } = this.state;
            form.floors[floor_no].details.push(
                {
                    'apartment_no':'',
                    'is_vacant':true,
                    'type':'',
                    'rent_amount':0,
                    'security_amount':0,
                    'maintenance_charges':0,
                    'electricity_charges':0,
                    'water_charges':0,
                    'gas_charges':0,
                    'amenities':{},
                    "tenants_allowed":0,
                    "tenants":[{
                        "tenant_id":"",
                        "start_date":"",
                        "end_date":""
                    }]
                }
            );
            this.setState({form});
        };
    };

    async _saveBuilding(e){
        e.preventDefault();
        let { form } = this.state;
        form['total_floors'] = form.floors.length;
        form['total_apartments'] = 0;
        for(let i = 0; i < form.floors.length; i++){
            form['total_apartments'] = form['total_apartments'] + parseInt(form.floors[i].apartments);
        };
        let _error = BUILDING_REQ_VALIDATOR(form);
        if(_error && _error.is_error){
            alert(_error.msg);
            return;
        } 
        try{
            let _resp = await Axios_Instance.post(`${ROUTES.add_building}`,form);
            if(!_resp && !_resp.data){_resp = {data:{success:false, msg:'Unexpected erro occured'} };}
            Notification({
                show: true,
                data:{success:_resp.data.success, msg:_resp.data.message || _resp.data.msg}
              });
        }catch(ex){
            Notification({
                show:true,
                data:ex.response ?ex.response.data : ex});

        }

    }

  render() {
            return (
                <div className="main-container">
                <div className="container">
                    <h4 className="page-title">
                        <p style={{'textTransform':'capitalize'}}>Add Channels/Pages</p>
                    </h4>

                    <div className="card">
                        <div className="row">
                            <div className="col-md-4 col-sm-4 col-xs-12">
                                <div className="input-wrap has-float-label">
                                    {/* <input type="text" name="name" className="form-control" onChange={this._onFormFieldChange.bind(this,'root',-1,-1)}/> */}
                                    <select name="user_type" required onChange={this._onFormFieldChange.bind(this)}  className="form-control">
                                        <option>--</option>
                                        <option value="1">Channel</option>
                                        <option value="2">Page</option>
                                    </select>
                                    <span>Type</span>
                                </div>
                            </div>
                            <div className="col-md-4 col-sm-4 col-xs-12">
                                <div className="input-wrap has-float-label">
                                <select name="user_type" required onChange={this._onFormFieldChange.bind(this)}  className="form-control">
                                    <option>--</option>
                                    <option value="1">Youtube </option>
                                    <option value="2">Facebook</option>
                                    <option value="3">Instagram</option>
                                    <option value="4">DailyMotion</option>
                                </select>
                                <span>Platform</span>
                                </div>
                            </div>
                            <div className="col-md-4 col-sm-4 col-xs-12">
                                <div className="input-wrap has-float-label">
                                    <input type="text" name="address2" className="form-control" min="1" onChange={this._onFormFieldChange.bind(this,'address',-1,-1)}/>
                                    <span>Address 2</span>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-md-4 col-sm-4 col-xs-12">
                                <div className="input-wrap has-float-label">
                                    <input type="text" name="pincode" required={true} className="form-control" min="1" onChange={this._onFormFieldChange.bind(this,'address',-1,-1)}/>
                                    <span>Pincode*</span>
                                </div>
                            </div>
                            <div className="col-md-4 col-sm-4 col-xs-12">
                                <div className="input-wrap has-float-label">
                                    <input type="text" name="registered_owner_name" required={true} className="form-control" onChange={this._onFormFieldChange.bind(this,'root',-1,-1)}/>
                                    <span>Registered Owner Name*</span>
                                </div>
                            </div>
                            <div className="col-md-4 col-sm-4 col-xs-12">
                                <div className="input-wrap has-float-label">
                                    {/* <input type="number" name="mobile" className="form-control" min="1" />
                                    <span>Address 2</span> */}
                                </div>
                            </div>
                        </div> 
                    </div>

                    <div className="card">
                        <p>Floor Details</p>
                        <br />
                        {this.state.form.floors.map((floor, key)=>
                            <div className="floor_section" key={key}>
                                <div className="row">
                                    <div className="col-md-4 col-sm-4 col-xs-12">
                                        <div className="input-wrap has-float-label">
                                            <input type="text" name="floor_no" required={true} readOnly={true} disabled={true} defaultValue={key+1} className="form-control" onChange={this._onFormFieldChange.bind(this,'floor',key,-1)} />
                                            <span>Floor No.*</span>
                                        </div>
                                    </div>
                                    <div className="col-md-4 col-sm-4 col-xs-12">
                                        <div className="input-wrap has-float-label">
                                            <input type="number" name="apartments" required={true} className="form-control" onChange={this._onFormFieldChange.bind(this,'floor',key,-1)}/>
                                            <span>Total Apartments/Rooms in this floor*</span>
                                        </div>
                                    </div>
                                    <div className="col-md-4 col-sm-4 col-xs-12"></div>
                                </div>
                                <div>
                                    <span>Apartment/Room Details</span>
                                    <span className="pull-right"><button className="btn btn-outline-primary" onClick={this._addApartment.bind(this, key)}>Add Apartment</button></span>
                                    </div><br />
                                    {floor.details.map((apartment, k)=>
                                        <div key={k}>
                                            <div className="row">
                                                <div className="col-md-4 col-sm-4 col-xs-12">
                                                    <div className="input-wrap has-float-label">
                                                        <input type="text" name="apartment_no" required={true} className="form-control" onChange={this._onFormFieldChange.bind(this,'aptmnt',key,k)}/>
                                                        <span>Aprtment No./Name*</span>
                                                    </div>
                                                </div>
                                                <div className="col-md-4 col-sm-4 col-xs-12">
                                                    <div className="input-wrap has-float-label">
                                                        <select className="form-control" name="is_vacant" onChange={this._onFormFieldChange.bind(this,'aptmnt',key,k)}>
                                                            <option value="">-- Choose --</option>
                                                            <option value="true">Yes</option>
                                                            <option value="false">No</option>
                                                        </select>
                                                        <span>Is Vacant</span>
                                                    </div>
                                                </div>
                                                <div className="col-md-4 col-sm-4 col-xs-12">
                                                    <div className="input-wrap has-float-label">
                                                        <select className="form-control" name="type" onChange={this._onFormFieldChange.bind(this,'aptmnt',key,k)}>
                                                            <option value="">-- Choose --</option>
                                                            <option value="unfurnished">UnFurnished</option>
                                                            <option value="semifurnished">SemiFurnished</option>
                                                            <option value="furnished">Furnished</option>
                                                        </select>
                                                        <span>Type</span>
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="row">
                                                <div className="col-md-4 col-sm-4 col-xs-12">
                                                    <div className="input-wrap has-float-label">
                                                        <input type="text" name="rent_amount" className="form-control" onChange={this._onFormFieldChange.bind(this,'aptmnt',key,k)} />
                                                        <span>Monthly Rent</span>
                                                    </div>
                                                </div>
                                                <div className="col-md-4 col-sm-4 col-xs-12">
                                                    <div className="input-wrap has-float-label">
                                                        <input type="number" name="security_amount" className="form-control" onChange={this._onFormFieldChange.bind(this,'aptmnt',key,k)}/>
                                                        <span>Security Amount</span>
                                                    </div>
                                                </div>
                                                <div className="col-md-4 col-sm-4 col-xs-12">
                                                    <div className="input-wrap has-float-label">
                                                        <input type="number" name="maintenance_charges" className="form-control" onChange={this._onFormFieldChange.bind(this,'aptmnt',key,k)} />
                                                        <span>Maintenance Charges</span>
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="row">
                                                <div className="col-md-4 col-sm-4 col-xs-12">
                                                    <div className="input-wrap has-float-label">
                                                        <input type="text" name="electricity_charges" className="form-control" onChange={this._onFormFieldChange.bind(this,'aptmnt',key,k)} />
                                                        <span>Electricity Charges per unit</span>
                                                    </div>
                                                </div>
                                                <div className="col-md-4 col-sm-4 col-xs-12">
                                                    <div className="input-wrap has-float-label">
                                                        <input type="number" name="water_charges" className="form-control" onChange={this._onFormFieldChange.bind(this,'aptmnt',key,k)}/>
                                                        <span>Water Charges</span>
                                                    </div>
                                                </div>
                                                <div className="col-md-4 col-sm-4 col-xs-12">
                                                    <div className="input-wrap has-float-label">
                                                        <input type="number" name="gas_charges" className="form-control" onChange={this._onFormFieldChange.bind(this,'aptmnt',key,k)}/>
                                                        <span>Gas Charges</span>
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="row">
                                                <div className="col-md-4 col-sm-4 col-xs-12">
                                                    <div className="input-wrap has-float-label">
                                                        <input type="text" name="tenants_allowed" className="form-control" onChange={this._onFormFieldChange.bind(this,'aptmnt',key,k)}/>
                                                        <span>Tenants Allowed</span>
                                                    </div>
                                                </div>
                                                <div className="col-md-4 col-sm-4 col-xs-12">
                                                    <div className="input-wrap has-float-label">
                                                        {/* <input type="number" name="mobile" className="form-control" />
                                                        <span>Water Charges</span> */}
                                                    </div>
                                                </div>
                                                <div className="col-md-4 col-sm-4 col-xs-12">
                                                    <div className="input-wrap has-float-label">
                                                        {/* <input type="number" name="mobile" className="form-control" />
                                                        <span>Gas Charges</span> */}
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                )}

                            </div>

                        )}
                        {!this.state.form.floors.length && (<h5 className="text-center" style={{'color':'#ccc'}}> No Floor Added</h5>   )}
                        <button className="btn btn-outline-primary" onClick={this._appendFloorDetails.bind(this)}>Add Floor</button>

                    </div>
                        <div className="pull-right">
                                <button className="btn btn-primary" onClick={this._saveBuilding.bind(this)}>Save Building</button>
                        </div>
                </div>
                </div>
            );
    }

}

export default withRouter(AddBuilding);
